      //\' neighbors to brute force.\')\n\nDO ii = 1, num_run\n  CALL RANDOM_NUMBER(areal)\n  indx = FLOOR(areal * n) + 1\n  qv = input_data(1:d, indx)\n  res_tree(:)%idx = -666\n  res_bforce(:)%idx = -777\n\n  CALL kdtree2_n_nearest_brute_force(tp=kd1, qv=qv, nn=nn, &\n                                     results=res_bforce)\n  CALL kdtree2_n_nearest(tp=kd1, qv=qv, nn=nn, results=res_tree)\n\n  maxdeviation = MAXVAL(ABS(res_tree(1:nn)%dis - res_bforce(1:nn)%dis))\n  problem = ANY(res_tree(1:nn)%idx .NE. res_bforce(1:nn)%idx) .OR. &\n            (maxdeviation .GT. 1.0E-8)\n\n  CALL OK(.NOT. problem, "kdtree2_n_nearest(): ")\n  IF (problem) THEN\n    CALL Display(\'MISMATCH! @ : \'//tostring(ii))\n    CALL Display(res_tree(1:nn)%idx, "Tree indices: ")\n    CALL Display(res_bforce(1:nn)%idx, "Brute indices: ")\n    CALL Display(res_tree(1:nn)%dis - res_bforce(1:nn)%dis, &\n                 "Tree-brute distances: ")\n\n  END IF\n\nEND DO\n\nCALL kdtree2_destroy(kd1)\n\nEND PROGRAM main\n'})})}function i(e={}){const{wrapper:n}={...(0,a.R)(),...e.components};return n?(0,s.jsx)(n,{...e,children:(0,s.jsx)(d,{...e})}):d(e)}const o={},c="Kdtree2_n_nearese",l={},_=[{value:"Interface",id:"interface",level:2}];function u(e){const n={code:"code",h1:"h1",h2:"h2",header:"header",p:"p",pre:"pre",...(0,a.R)(),...e.components},{TabItem:t,Tabs:r}=n;return t||m("TabItem",!0),r||m("Tabs",!0),(0,s.jsxs)(s.Fragment,{children:[(0,s.jsx)(n.header,{children:(0,s.jsx)(n.h1,{id:"kdtree2_n_nearese",children:"Kdtree2_n_nearese"})}),"\n",(0,s.jsx)(n.p,{children:"Find the 'nn' vectors in the tree nearest to 'qv' in euclidean norm\nreturning their indexes and distances in 'indexes' and 'distances'\narrays already allocated passed to this subroutine."}),"\n",(0,s.jsx)(n.h2,{id:"interface",children:"Interface"}),"\n",(0,s.jsxs)(r,{children:[(0,s.jsx)(t,{value:"interface",label:"Interface",default:!0,children:(0,s.jsx)(n.pre,{children:(0,s.jsx)(n.code,{className:"language-fortran",children:"SUBROUTINE